#1.compte combien il y à de handle dans la liste
def nombre_handle(twitter)
puts "il y a #{twitter.size} handle dans cette liste."
end

#2.me dis quel est le handle le plus court de la liste
def lepluscourt(twitter)
    puts "le handle le plus court de la liste est #{twitter.min_by(&:length)}"
end

#3.me compte combien il y a de handles avec 5
def caractere_5(twitter)
handle5 = twitter.select { |handle| handle[1..-1].length == 5 } 
    puts "Il y a #{handle5.size} handles avec 5 caractères."
end

#4.me mets seulement les handles qui commencent par une majuscule
def majuscule(twitter)
majuscule = twitter.count { |h| h[1] =~ /[A-Z]/ } 
    puts "Il y a #{majuscule} handles qui commencent par une majuscule."
end

#5.trie la liste des handles par ordre alphabétique
def liste_ordre(twitter)
handle_ordre = twitter.sort
    puts "Handles triés par ordre alphabétique : #{handle_ordre}"
end

#6.trie la liste des handes par tailles
def liste_tailles_handles(twitter)
handle_taille = twitter.sort_by { |handle| -handle.length }
    puts "Voici la taille des handles triés du plus petit au plus grand : #{handle_taille}"
end


#7.calcule la position dans l'array de la personne @epenser
def position_handle(twitter, email)
position = twitter.index(email)
    puts "Le handle #{email} se trouve à la position #{position}" if position
end

#8.trie le liste par tailles des handles par nombres de caractères
def liste_tailles_caractère(twitter)
    handle_caractere = twitter.sort_by { |handle| -handle.length }
        puts "Voici la taille des handles triés du plus petit au plus grand : #{handle_caractere}"
    handle_caractere.each do |handle|
            puts "#{handle} - #{handle.length} caractères"
    end
end

